export const handle = async (request, response) => {
    // Do not remove the existing wrapper code, else your code might not execute properly
    // Add your code below this line - sample given below

    // Input variables defined for the function are available in request.data, as shown below
    // const [myInputVar1, myInputVar2] = [request.inputs.myInputVar1, request.inputs.myInputVar2];

    // Output variables need to be set into response.data, as shown below
    // response.data = {'myOutputVar1': myInputVar1 + myInputVar2, 'myOutputVar2': 'String data'};

    // Return the response object
    const contactInfo = request.inputs.contactInfo;
    if (contactInfo) {
        const contactParts = contactInfo.split(':');
        const removeAgentBody = {
            campaignId: contactParts[0],
            segmentId: contactParts[1],
            contactId: contactParts[2],
            cciContactId: contactParts[3]
        }
        response.data = {
            removeAgentBody: removeAgentBody
        };
    }
    return response;
}
